INSERT INTO tours (tour_id, tour_title, tour_reviews, tour_price, tour_discount_rate, tour_quantity, tour_place, tour_type, tour_region, tour_times) VALUE 
(1, 'Tour Xe Lửa Quy Nhơn 3N4Đ: KDL Hầm Hô - Kỳ Co - Eo Gió - Chùa Ông Núi', '<p lang="en-US" link="#0000ff" vlink="#800000" dir="ltr"> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Quê hương xứ Nẫu Quy Nhơn</font> </font> </font> </h3> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Xứ Nẫu hay Quy Nhơn (Bình Định) được thiên nhiên ban tặng phong cảnh nên thơ, hữu tình và những bãi biển trải dài tuyệt đẹp. Quy Nhơn hội tụ đầy đủ các yếu tố của một điểm nghĩ dưỡng hàng đầu với những bãi biển trong xanh, các khu resorts đẳng cấp quốc tế, ẩm thực đặc sắc cùng với các danh lam thắng cảnh như Kỳ Co - Eo Gió được ví như Maldives phiên bản Việt, chùa ông Núi với tượng phật lớn nhất Đông Nam Á hay Tháp Đôi - một kiến trúc Champa độc đáo được xây dựng từ thế kỷ 12.</font> </font> </font> </font> </p> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font color="#3f3b3b">&nbsp;</font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Những trải nghiệm thú vị trong chương trình</font> </font> </font> </h3> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Khu du lịch Ghềnh Ráng:</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">&nbsp;kết hợp hài hòa giữa trời, mây, non nước, một bên là biển mênh mông, một bên là núi đá muôn hình vạn trạng. Từ đây du khách có thể ngắm nhìn gần trọn hết vẻ đẹp đầy sức sống của phố biển Quy Nhơn.</font> </font> </font> </font> </p> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Bãi tắm Kỳ Co:</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">&nbsp;Với&nbsp;dải cát vàng ôm lấy biển, bọc thành một hình cung, mềm mại như dải lụa uốn lượn, ôm trọn lấy làn nước trong xanh tận đáy. Nơi này được ví như là Maldives của Việt Nam.</font> </font> </font> </font> </p> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Khu Du Lịch Hầm Hô</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">&nbsp;hoà mình vào thiên nhiên giữa khung cảnh núi non và sông suối hùng vỹ, tận hưởng không khí trong lành.&nbsp;</font> </font> </font> </font> </p> <p style="line-height: 108%; margin-bottom: 0.11in"><br /> <br /> </p> </p>', 3650000, 10, 10, 'Quy Nhơn', 'Du lịch biển', 'Miền Trung', '3 ngày 4 đêm'),
(2, 'Tour Ninh Bình 1N : Hà Nội - Bái Đính - Tràng An', '<p lang="en-US" link="#0000ff" vlink="#800000" dir="ltr"> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Khám phá Ninh Bình vùng đất Cố đô cùng khung cảnh nên thơ hữu tình</font> </font> </font> </h3> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Ninh Bình với nhiều danh thắng đẹp cả về cảnh quan và ý nghĩa lịch sử, đã trở thành điểm đến được nhiều du khách lựa chọn. Nói nơi đây là 1 Việt Nam thu nhỏ quả không sai bởi nó hòa quyện toàn bộ đặc trưng văn hóa, thiên nhiên và lịch sử. Cùng iVIVU khám phá điểm đặc trưng này, tại sao không? Cùng iVIVU khám phá ngay hôm nay!</font> </font> </font> </font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Những trải nghiệm thú vị trong chương trình</font> </font> </font> </h3> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Chùa Bái Đính</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">: Ngôi chùa lớn nhất và nắm giữ nhiều kỷ lục nhất Việt Nam.</font> </font> </font> </font> </p> <p align="justify" style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Khu Du Lịch Sinh Thái Tràng An</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">: Phong cảnh thiên nhiên hoang sơ, tuyệt đẹp của Tràng An đã được UNESCO công nhận là di sản thiên nhiên và di sản văn hóa thế giới. Khung cảnh nơi đây được tạo nên từ dòng sông chạy uốn lượn qua các dãy núi đá vôi, tạo thành vô vàn những hang động tự nhiên huyền ảo, kỳ bí.</font> </font> </font> </font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Bạn có sẵn sàng</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Khám phá chùa Bái Đính có thể đi bộ nhiều nên quý khách chuẩn bị giày đế bằng, thấp và thoải mái, dù (ô) hoặc nón.</font> </font> </font> </font> </p> <p style="line-height: 108%; margin-bottom: 0.11in"><br /> <br /> </p> </p>', 700000, 5, 10, 'Ninh Bình', 'Du lịch thông thường', 'Miền Bắc', 'Trong ngày'),
(3, 'Trải Nghiệm Du Lịch Mạo Hiểm Đỉnh Cao: Chinh Phục Hang Sụt Sâu Nhất Hành Tinh', '<p lang="en-US" link="#0000ff" vlink="#800000" dir="ltr"> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Chinh Phục Kong Collapse - Hố Sụt Sâu Nhất Hành Tinh</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Bạn là người ưa thích du lịch mạo hiểm, yêu thiên nhiên hay muốn thử thách bản thân? Đây có thể là trải nghiệm mà bạn đang tìm kiếm, một hành trình chỉ dành cho những người thực sự đam mê khám phá và muốn chinh phục những thử thách cực đại để có được những trải nghiệm để đời, bức phá&nbsp;giới hạn của bản thân. Cùng iVIVU khám phá những hang động và cánh rừng nguyên sinh tuyệt đẹp tại vùng lõi của Di sản thiên nhiên thế giới Vườn quốc gia Phong Nha – Kẻ Bàng và các khu vực lân cận.&nbsp;Chinh phục&nbsp;hang Pygmy, hang động lớn thứ&nbsp;tư thế giới và khám phá Kong Collapse - một trong những hố sụt sâu nhất hành tinh với trải nghiệm đu dây 100m đầy thách thức!&nbsp;</font> </font> </font> </font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Những trải nghiệm thú vị trong chương trình</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">- Trở thành một trong những người đầu tiên khám phá hố sụt Kong với trải nghiệm đu dây từ độ cao 100m,&nbsp;một trong những hố sụt sâu nhất hành tinh với độ sâu hơn 450m.</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;Kết hợp tuyệt vời các trải nghiệm như đi bộ giữa rừng nguyên sinh, leo trèo qua các tảng đá, bơi trong hang tối, cắm trại trong hang và đặc biệt là hoạt động đu dây khám phá hang động.</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;Hang Pygmy được đánh giá là hang động lớn thứ tư thế giới với vòm hang rộng và cao 100m</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">- Xây dựng kỹ năng khám phá hang động và các kỹ năng an toàn.</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font color="#3f3b3b">&nbsp;</font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Bạn có sẵn sàng</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Tour khám phá không thích hợp với du khách có tiền sử các bệnh về tim mạch, huyết áp cao, hen suyễn,các bệnh về xương khớp và du khách có triệu chứng sợ độ cao và bóng tối . Du khách sẽ thực hiện trả lời một số câu hỏi liên quan đến sức khỏe và được đội ngũ chuyên gia y tế của&nbsp;công ty&nbsp;kiểm tra sức khỏe trước khi tour khởi hành</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Cung đường bao gồm:</font> </font> </b></font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Đi bộ: 23 km</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Khám phá hang động: 7 km</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Đu dây: 100 m</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Bơi 300 m&nbsp;</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Cấp độ mạo hiểm: cực kỳ khó</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Độ tuổi: từ 16 đến 65</font> </font> </font> </font> </p> <p style="line-height: 108%; margin-bottom: 0.11in"><br /> <br /> </p> </p>', 35000000, 1, 10, 'Hang Sụt', 'Du lịch mạo hiểm', 'Miền Bắc', 'Trong ngày'),
(4, 'Tour Đà Nẵng 1N: Bà Nà Hill - Đường Lên Tiên Cảnh', '<p lang="en-US" link="#0000ff" vlink="#800000" dir="ltr"> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Châu Âu Thu Nhỏ</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Bà Nà Hills được biết đến là một trong những địa điểm nổi tiếng bậc nhất tại Việt Nam mà bất kỳ du khách nào cũng không thể bỏ qua. Nơi này được ví von như chốn ‘’tiên cảnh’’, như một ‘’châu Âu thu nhỏ trong lòng thành phố’’ với những công trình kiến trúc cổ đẹp ma mị, những trò chơi giải trí cực kỳ hấp dẫn hay cả một vườn hoa thơm ngát ngào ngạt chờ đón du khách đến chụp hình. Cùng iVIVU khám phá ngay hôm nay!&nbsp;</font> </font> </font> </font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Những trải nghiệm thú vị trong chương trình</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Bà Nà được ví von như Đà Lạt của miền trung hay ‘’lá phổi xanh’’, ‘’hòn ngọc khí hậu’’ của Việt Nam</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Fantasy</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">&nbsp;chia làm 3 tầng với hàng ngàn trò chơi từ nhẹ nhàng tới mạo hiểm với tổng số hơn 90 trò chơi miễn phí như phi công Skiver, tháp rơi tự do, công viên kỷ Jura, ngôi nhà ma ám, đua xe Outrun, cối xay gió, khu rừng thần tiên, vòng đua tử thần 4D,…</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;Đến với&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">ngôi làng Pháp</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">, các bạn sẽ được trải nghiệm một nước Pháp đầy lãng mạn và cổ điển với dấu ấn phong cách từ thế kỷ thứ 20 với những công trình kiến trúc độc đáo như hàng loạt lâu đài lãng mạn, Thánh đường St Denis, Brittany, ngôi làng nhỏ nhắn Apremont sur Allier, quảng trường, khách sạn, làng mạc, thị trấn…</font> </font> </font> </font> </p> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">-&nbsp;</font> </font><b> <font color="#3f3b3b"> <font face="Helvetica, serif">Cây cầu Vàng</font> </font> </b> <font color="#3f3b3b"> <font face="Helvetica, serif">&nbsp;đã trở thành một hiện tượng thu hút sự chú ý của đông đảo người đam mê du lịch trong nước cũng như quốc tế.</font> </font> </font> </font> </p> <h3 class="western" style="margin-top: 0.16in; margin-bottom: 0.1in; background: #ffffff"> <font color="#003c71"> <font face="Helvetica, serif"> <font size="5" style="font-size: 19pt">Bạn có sẵn sàng</font> </font> </font> </h3> <p style="line-height: 100%; margin-bottom: 0.1in; background: #ffffff"> <font face="Times New Roman, serif"> <font size="3" style="font-size: 12pt"> <font color="#3f3b3b"> <font face="Helvetica, serif">Khám phá Bà Nà&nbsp;có thể đi bộ nhiều nên quý khách chuẩn bị giày đế bằng, thấp và thoải mái, dù (ô) hoặc nón.</font> </font> </font> </font> </p> <p style="line-height: 108%; margin-bottom: 0.11in"><br /> <br /> </p> </p>', 1390000, 0, 10, 'Đà Nẵng', 'Du lịch thông thường', 'Miền Trung', 'Trong ngày'),
(5,'Tour Đà Lạt 1N: Du lịch Hà Nôi - Sapa - Bản Cát Cát - Check in Moana từ Hà Nội 2023','<p><strong>NGÀY 1 |&nbsp;Hà Nội – Sapa – Bản Cát Cát (Ăn Trưa/Tối)</strong></p><p class="ql-align-justify"><strong>6h30&nbsp;:</strong>&nbsp;Xe đón Quý khách tại điểm hẹn tại điểm đón trả của nhà xe khởi hành đi Sapa. Sau đó khởi hành đi Sa Pa bằng xe giường nằm chất lượng cao.</p><p class="ql-align-justify"><strong>9h00:</strong>&nbsp;Xe dừng chân dọc đường cao tốc Nội Bài – Lào Cai để quý khách nghỉ ngơi ăn sáng.</p><p class="ql-align-justify"><strong>12h30:</strong>&nbsp;Xe đến thị trấn Sa Pa nơi có rất nhiều dân tộc sinh sống như H’mong, Dao, Tày,… Hướng dẫn viên sẽ đưa Quý khách về khách sạn:</p><p class="ql-align-justify">Ăn trưa tại khách sạn sau đó nhận phòng và nghỉ ngơi</p><p class="ql-align-justify"><strong>14h30:</strong>&nbsp;HDV đưa quý khách đi bộ thăm bản Cát Cát nơi sinh sống của người H’mong đen, một nơi phòng cảnh hữu tình tại thung lũng nằm dưới chân đỉnh Phanxipang hùng vĩ. HDV địa phương sẽ giới thiệu cho quý khách cuộc sống thường ngày của người dân tộc nơi đây, sau đó quý khách tiếp tục đi bộ đến thác nước Cát Cát, trạm thủy điện Cát Cát của người Pháp xây dựng từ những năm đầu thế kỉ XX. Quý khách vui chơi chụp hình lưu niệm.</p><p class="ql-align-justify"><strong>17h30</strong>: Xe đón quý khách từ Bản Cát Cát quay trở lại thị trấn Sa Pa.</p><p class="ql-align-justify"><strong>19h00</strong>: Quý khách dùng bữa tối. Sau đó tự do khám phá thị trấn Sapa về đêm: Thăm nhà thờ đá Sapa, đi chợ ẩm thực Sapa và thưởng thức những món nướng đặc trưng của vùng đất này,… Ngủ đêm tại Sapa.</p><p><strong>NGÀY 2 |&nbsp;Sapa – Check in Moana – Hà Nội (Ăn Sáng/Trưa)</strong></p><p class="ql-align-justify"><strong>7h00</strong>: Quý khách dùng bữa sáng tại khách sạn.&nbsp;</p><p class="ql-align-justify"><strong>8h00</strong>: Sau bữa ăn sáng: HDV cùng quý khách đi bộ khám phá góc sống ảo Sapa. Khu check in Moana Sapa là một điểm đến ở Sapa còn khá mới mẻ. Với tổng diện tích lên đến 10.000m2 cùng tổ hợp các hạng mục được thực hiện công phu. Hạng mục đầu tiên được nhiều du khách quan tâm chính là cổng trời Bali. Đây là một chiếc cổng được lấy cảm hứng từ cổng trời của thành phố Bali xinh đẹp. Với thiết kế mô phỏng bản gốc cùng vị trí hướng thẳng ra núi rừng mênh mông, nơi đây sẽ tạo cho bạn nhiều ảnh chụp mang vẻ đẹp thiêng liêng, huyền bí. Cánh cổng được xây dựng công phu, phía trước là mặt sàn lát gạch bóng loáng để tạo nên dấu ấn hoàng tráng….Ngoài các góc check in đẹp, độc và mới mẻ, điểm đến này còn được yêu thích vì sở hữu view hướng thẳng ra dãy núi Hoàng Liên Sơn và đỉnh núi Fanxipan. Đến đây, bạn vừa được chụp hình thỏa thích, vừa được ngắm bức tranh lãng đãng mây trời đầy huyền ảo của Sapa.&nbsp;</p><p class="ql-align-justify"><strong>11h00:</strong>&nbsp;Quý khách quay về thị trấn.&nbsp;</p><p class="ql-align-justify"><strong>11h30:</strong>&nbsp;Quý khách dùng bữa trưa tại khách sạn. Làm thủ tục trả phòng.</p><p class="ql-align-justify">Sau bữa trưa Quý khách tự do mua sắm đặc sản địa phương về làm quà cho người thân.&nbsp;</p><p class="ql-align-justify"><strong>13h30-14h00</strong>: Quý khách lên xe khởi hành về lại Hà Nội.&nbsp;</p><p class="ql-align-justify"><strong>20h00-20h30</strong>: Về đến Hà Nội. Xe đưa Quý khách về tại điểm đón trả của nhà xe, kết thúc hành trình khám phá thị trấn trong sương 2 ngày 1 đêm đầy thú vị. Tạm biệt và hẹn gặp lại Quý khách trong những hành trình du lịch lần sau.</p><h1><br></h1><p><br></p><p><br></p>',2090000,3,10,'Hà Nội','Du lịch thông thường','Miền Bắc','2 ngày 1 đêm'),
(6,'Du lịch Hà Nội - Chùa Tam Chúc - Ninh Bình - Chùa Bái Đính - Tràng An','<h1><br></h1><p><br></p><p><strong>NGÀY 1 |&nbsp;TP.HCM – HÀ NỘI – CHÙA TAM CHÚC – NINH BÌNH (Ăn trưa, chiều)</strong></p><p class="ql-align-justify"><strong>Sáng:</strong>&nbsp;Quý khách có mặt tại ga quốc nội, sân bay Tân Sơn Nhất trước giờ bay ba tiếng.</p><ul><li class="ql-align-justify">Đại diện công ty&nbsp;<strong>Du Lịch Việt</strong>&nbsp;đón và hỗ trợ Quý Khách làm thủ tục đáp chuyến bay đi&nbsp;<strong>Hà Nội</strong>.</li><li class="ql-align-justify">Đến sân bay&nbsp;<strong>Nội Bài</strong>, Hướng dẫn viên đón đoàn khởi hành đến<strong>&nbsp;Ninh Bình,&nbsp;</strong>nơi có danh thắng<strong>&nbsp;Tràng An&nbsp;</strong>được<strong>&nbsp;UNESCO&nbsp;</strong>công nhận là di sản hỗn hợp văn hóa và thiên nhiên của thế giới.</li></ul><p class="ql-align-justify"><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li class="ql-align-justify">Thăm&nbsp;<strong>Chùa Tam Chúc</strong>, ngôi chùa lớn nhất thế giới. Với diện tích quần thể lên tới 5.000 ha, được tạc 1.200 bức tượng bằng dung nham núi lửa và sở hữu nhiều báu vật trên thế giới.</li></ul><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Quần thể Tam Chúc với cảnh quan hùng vĩ, phía trước, giữa lòng hồ là 6 quả núi với hình dạng quả chuông, phía sau là 7 ngọn núi có thể phát sáng khi có ánh trăng ban đêm tạo nên thế tiền lục nhạc, hậu thất tinh.</li></ul><p class="ql-align-justify"><strong>Tối:</strong>&nbsp;&nbsp;&nbsp;&nbsp;Dùng bữa tối. Nghỉ đêm tại&nbsp;<strong>Ninh Bình.</strong></p><ul><li class="ql-align-justify">Quý khách có thể tự do dạo phố, thưởng thức các món đặc sản Ninh Binh như thịt dê núi, ốc núi, nem Yên Mạc, cơm cháy.</li></ul><p><strong>NGÀY 2 |&nbsp;TRÀNG AN – TUYỆT TÌNH CỐC – HANG MÚA (Ăn sáng, trưa, chiều)</strong></p><p>Sáng:&nbsp;&nbsp;Dùng buffet sáng tại khách sạn.</p><ul><li>Đoàn xuôi thuyền đi dọc theo suối giữa cánh đồng lúa thăm&nbsp;<strong>Khu du lịch Tràng An</strong>&nbsp;nơi những dải đá vôi, thung lũng và những sông ngòi đan xen tạo nên một không gian huyền ảo, kỳ bí, quý khách đi đò thăm&nbsp;<strong>Hang sáng, Hang tối, Hang nấu rượu</strong>&nbsp;và tìm hiểu văn hóa lịch sử nơi đây.</li></ul><p class="ql-align-center"><br></p><p><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li>Ghé thăm<strong>&nbsp;Tuyệt Tình Cốc,&nbsp;</strong>nơi cảnh vật hoang sơ, bình yên thơ mộng bốn bề là núi đá phía dưới là non nước xanh biếc 1 màu, cùng với đó là sự góp mặt của rất nhiều hang động lớn nhỏ tạo lên vẻ đẹp huyền bí mà không nơi đâu có được như nơi đây, điển hình trong số đó phải kể đến&nbsp;<strong>Động Am Tiêm</strong>, động nằm ở lưng chừng núi đường đi rất hiểm trở phải leo qua 205 bậc đá mới lên tới nơi.</li></ul><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li>Quý khách đến&nbsp;<strong>Hang Múa</strong>&nbsp;– được mệnh danh là&nbsp;<strong>Vạn Lý Trường Thành</strong>&nbsp;thu nhỏ. Quý khách có thể chinh phục hơn 400 bậc đá để ngắm toàn bộ khung cảnh&nbsp;Tam Cốc (Một Hạ Long Trên Cạn) từ trên cao.&nbsp;Đây là điểm đến thu hút hàng trăm nghìn các bạn trẻ khắp nơi thường xuyên đến đây để chụp ảnh, check in…</li></ul><p><strong>Tối:</strong>&nbsp;&nbsp;&nbsp;&nbsp;Dùng bữa tối. Nghỉ đêm tại&nbsp;<strong>Ninh Bình.</strong></p><p><strong>NGÀY 3 |&nbsp;CHÙA BÁI ĐÍNH – HÀ NỘI – TP.HCM (Ăn sáng, trưa)</strong></p><p>Sáng:&nbsp;&nbsp;Dùng buffet sáng tại khách sạn.</p><ul><li>Tham quan&nbsp;<strong>chùa Bái Đính</strong>&nbsp;– ngôi chùa có nhiều kỷ lục nhất Việt Nam (ngôi chùa có diện tích rộng nhất – Tượng Phật bằng đồng lớn nhất Việt Nam).</li></ul><p class="ql-align-center"><br></p><ul><li>Đoàn trở về&nbsp;<strong>Hà Nội.</strong></li></ul><p><strong>Trưa:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>Dùng bữa trưa.</p><ul><li>Đoàn tự do tham quan&nbsp;<strong>Hồ Hoàn Kiếm</strong>,&nbsp;<strong>Đền Ngọc Sơn.</strong></li><li>Hướng dẫn viên tiễn đoàn ra&nbsp;<strong>sân bay Nội Bài</strong>&nbsp;đón chuyến bay về TP.HCM.</li><li>Kết thúc chuyến tham quan, chia tay đoàn và hẹn gặp lại.</li></ul><h1><br></h1><p><br></p>',6599000,0,10,'Ninh Bình','Du lịch thông thường','Miền Bắc','3 ngày 2 đêm'),
(7,'Tour Du lịch Đông Bắc Hà Nội - Hà Giang - Cao Bằng','<p><strong>NGÀY 1 |&nbsp;TP.HCM – HÀ NỘI – PHÚ THỌ – HÀ GIANG (Ăn trưa, chiều)</strong></p><p>Sáng:&nbsp;Quý khách có mặt tại ga quốc nội, sân bay Tân Sơn Nhất trước giờ bay ít nhất ba tiếng.</p><ul><li>Đại diện công ty Du Lịch Việt đón và hỗ trợ Quý khách làm thủ tục đón chuyến bay đi&nbsp;<strong>Hà Nội.</strong></li><li>Đến sân bay&nbsp;<strong>Nội Bài</strong>, Hướng dẫn viên đón Quý khách khởi hành đến&nbsp;<strong>Hà Giang.</strong></li></ul><p><strong>Trưa:</strong>&nbsp;Dừng chân, dùng cơm trưa.</p><ul><li class="ql-align-justify">Đoàn tiếp tục khởi hành đến&nbsp;<strong>Hà Giang</strong>&nbsp;– nơi có những con đường đèo, cứ nối nhau quanh co uốn lượn, nơi có những con người dân tộc chân chất, mặc dù cuộc sống nghèo khổ nhưng trên môi luôn nở nụ cười.</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Tham quan Làng văn hóa du lịch sinh thái&nbsp;<strong>Hạ Thành,&nbsp;</strong>được bao quanh bởi những thửa ruộng bậc thang xếp nối nhau. Đến với&nbsp;<strong>Hạ Thành</strong>&nbsp;là đến với những ngôi nhà sàn truyền thống nguyên sơ, đến với những câu hát lượn, hát cọi, hát then ngọt ngào, những điệu múa dân gian, múa tín ngưỡng, những lễ hội truyền thống huyền bí, tạo cho bạn một cảm giác như trở về nơi cội nguồn của dân tộc.</li></ul><p><strong>Tối:&nbsp;</strong>Dùng cơm tối. Nghỉ đêm&nbsp;<strong>Hà Giang.</strong></p><p><strong>NGÀY 2 |&nbsp;HÀ GIANG – CỔNG TRỜI QUẢN BẠ – DINH VUA MÈO (Ăn sáng, trưa, chiều)</strong></p><p>Sáng:&nbsp;Dùng bữa sáng.</p><ul><li class="ql-align-justify">Rời thành phố&nbsp;<strong>Hà Giang</strong>, đường lên&nbsp;<strong>Quản Bạ</strong>&nbsp;mở ra giữa hai bên vách núi cao chất ngất, vượt vòng cua khúc khuỷu, đi lên đèo Pắc Sum. Đoàn dừng chân ngắm cảnh và chụp ảnh tại dốc Bắc Sum.</li><li class="ql-align-justify">Lên đến&nbsp;<strong>Cổng Trời Quản Bạ</strong>, Quý khách có thể ngắm nhìn thị trấn Tam Sơn thơ mộng.</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify"><strong>Núi đôi Quản Bạ</strong>&nbsp;hiện lên giữa những núi đá trùng điệp và ruộng bậc thang với hình dáng, thế đứng ngồ ngộ khiến du khách không khỏi ngỡ ngàng trước vẻ đẹp kỳ vĩ của tạo hoá. Hai trái núi gắn với truyền thuyết “Núi Cô Tiên” rất thi vị.</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Ghé thăm&nbsp;<strong>Phố Cáo</strong>, với những ngôi nhà tường màu đất vàng đặc trưng, cổng gỗ nằm trong hàng rào đá – nét đặc trưng của vùng cao nguyên đá với bốn dân tộc Mông, Hán, Dao, Pu Péo.</li><li class="ql-align-justify">Từ hướng&nbsp;<strong>Yên Minh</strong>&nbsp;lên thị trấn Đồng Văn, đến ngã ba Phó Bảng, khi đến đỉnh đèo nơi ngã ba đi về Phó Bảng, du khách có thể nhìn thấy toàn cảnh Sủng Là phía dưới thung lũng. Từ trên con đường vắt vẻo lưng chừng trời nhìn xuống, quý khách được dịp chiêm ngưỡng một bức tranh thiên nhiên thanh bình, xinh đẹp. Đến&nbsp;<strong>Sủng Là</strong>&nbsp;thăm nhà cổ H’mong nơi quay bộ phim "Chuyện của Pao".</li></ul><p><strong>Trưa:&nbsp;</strong>Dùng cơm trưa.</p><ul><li class="ql-align-justify">Đoàn đến&nbsp;<strong>khu di tích nhà vua mèo Vương Chí Sình</strong>&nbsp;một dinh thự của bậc đế vương xưa, là một công trình có kiến trúc đẹp, hiếm có và rất độc đáo của Hà Giang..</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Quý khách đến tham quan cột cờ&nbsp;<strong>Lũng Cú</strong>, mảnh đất địa đầu Tổ quốc. Đứng trên đỉnh cột cờ, Quý khách sẽ thấy toàn bộ làng văn hóa Lô Lô, phóng xa tầm mắt sẽ thấy những con đường nhỏ xíu, uốn lượn quanh những cánh đồng lúa, những ngôi nhà nhà lọt thỏm giữa màu xanh của cây cối.</li></ul><p><strong>Tối:&nbsp;</strong>Dùng cơm tối. Nghỉ đêm tại<strong>&nbsp;Đồng Văn</strong>.</p><p><strong>NGÀY 3 |&nbsp;ĐỒNG VĂN – MÈO VẠC – CAO BẰNG (Ăn sáng, trưa, chiều)</strong></p><p>Sáng:&nbsp;Dùng bữa sáng.</p><ul><li class="ql-align-justify">Đoàn tiếp tục khởi hành đến&nbsp;<strong>Mèo Vạc</strong>, trên đường dừng chân ngắm cảnh&nbsp;<strong>đèo Mã Phì Lèng</strong>, một trong&nbsp;<strong><em>"tứ đại đỉnh đèo"&nbsp;</em></strong>tại vùng núi phía Bắc Việt Nam, Mã Phì Lèng nằm giữa cao nguyên&nbsp;đá&nbsp;Đồng Văn, một bên là vách núi Mã Pí Lèng cao dựng đứng và một bên là&nbsp;<strong>sông Nho Quế</strong>&nbsp;thơ mộng.</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Đoàn lên thuyền,&nbsp;<strong><em>du thuyền trên Hẻm Tu sản – Sông Nho Quế</em></strong><em>.</em>&nbsp;Sông Nho Quế chảy vào Việt Nam từ địa phận thôn Séo Lủng, xã Lũng Cú, huyện Đồng Văn đi qua Hẻm núi Tu Sản rồi chảy dọc đèo Mã Pì Lèng, khi đến Mèo Vạc thì chảy theo hướng Đông vào địa phận Cao Bằng rồi đổ vào Sông Gâm.</li><li class="ql-align-justify"><strong><em>Hẻm vực Tu Sản</em></strong>&nbsp;được coi là hẻm vực sâu nhất Đông Nam Á và nằm trong thung lũng có kiến tạo địa chất độc nhất vô nhị ở Việt Nam. Hẻm vực mang vẻ đẹp nên thơ, kỳ bí như sợi chỉ màu ngọc bích huyền ảo, ẩn hiện giữa núi rừng Đông Bắc hiểm trở&nbsp;<em>(chi phí xe ôm và thuyền tự túc).</em></li></ul><p><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li class="ql-align-justify">Đến thị trấn&nbsp;<strong>Mèo Vạc</strong>, nếu vào đúng dịp chợ phiên (được mở vào chủ nhật hàng tuần), là nơi hội tụ tinh hoa văn hoá của các dân tộc thiểu số vùng cao nguyên đá của Hà Giang, chợ phiên Mèo Vạc ẩn chứa trong đó những nét độc đáo khó trộn lẫn với bất cứ chợ nào. Ngoài ra, Mèo Vạc còn hấp dẫn du khách nhờ những món đặc sản như: thịt bò khô, đậu xi, chè xanh Tát Ngà, rượu ngô Ha Ía và các loại thuốc dân gian truyền thống các dân tộc.</li></ul><p><strong>Tối:&nbsp;</strong>Đoàn đến Cao Bằng dùng cơm tối. Nghỉ đêm tại Cao Bằng.</p><p><strong>NGÀY 4 |&nbsp;CAO BẰNG – THÁC BẢN GIỐC – ĐỘNG NGƯỜM NGAO (Ăn sáng, trưa, chiều)</strong></p><p>Sáng:&nbsp;Dùng bữa sáng.</p><ul><li class="ql-align-justify">Đoàn tiếp tục đến tham quan&nbsp;<strong>thác Bản Giốc</strong>&nbsp;– nằm tại biên giới giữa Việt Nam và Trung Quốc, Quý khách cảm nhận được thiên nhiên hùng vĩ cảnh đẹp hữu tình, thác Bản Giốc là một trong ba thác nước tự nhiên 3 tầng đẹp nhất Đông Nam Á</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><p><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li>Đoàn khởi hành tham quan&nbsp;<strong>Động Ngườm Ngao</strong>, động đá vôi được hình thành cách đây 300 triệu năm trước Công Nguyên. Cùng với thời gian, những nhũ đá và măng đá đã tạo nên những khung cảnh thật sinh động, kì thú khiến con người phải thán phục, kinh ngạc.</li></ul><p><strong>Tối:&nbsp;</strong>Dùng cơm tối. Nghỉ đêm tại Cao Bằng.</p><p><strong>NGÀY 5 |&nbsp;CAO BẰNG – PẮC PÓ – BẮC KẠN – BA BỂ (Ăn sáng, trưa, chiều)</strong></p><p>Sáng:&nbsp;Dùng bữa sáng.</p><ul><li class="ql-align-justify">Tham quan&nbsp;<strong>di tích Pắc Pó</strong>, nơi Chủ tịch Hồ Chí Minh chọn làm chỗ ở sau hơn 30 năm bôn ba ở nước ngoài trở về Tổ quốc (08/02/1941) để trực tiếp lãnh đạo cách mạng Việt Nam với hang Bo Bam, bãi Cò Rạc, hang Cốc Pó, suối Lê-nin, núi Các Mác, suối Nậm.</li></ul><p class="ql-align-justify">&nbsp;</p><p class="ql-align-center"><br></p><p class="ql-align-center">&nbsp;</p><ul><li class="ql-align-justify">Đoàn ghé thăm Khu di tích anh hùng liệt sỹ&nbsp;<strong>Kim Đồng,&nbsp;</strong>thuộc Cụm di tích lịch sử cách mạng, nơi ghi nhớ công lao của người Anh hùng và khắc ghi nơi thành lập Đội nhi đồng cứu quốc đầu tiên.&nbsp;</li></ul><p><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li>Đoàn tiếp tục đến Khu du lịch Hồ Ba Bể.</li></ul><p><strong>Tối:&nbsp;</strong>Dùng cơm tối. Nghỉ đêm tại Khu du lịch Ba Bể.</p><p><strong>NGÀY 6 |&nbsp;KHU DU LỊCH HỒ BA BỂ – HÀ NỘI – TP.HCM (Ăn sáng, trưa)</strong></p><p>Sáng:&nbsp;Dùng bữa sáng.</p><ul><li class="ql-align-justify">Đoàn xuống thuyền khám phá phong cảnh thiên nhiên kỳ thú của&nbsp;<strong>hồ Ba Bể</strong>&nbsp;- được ví như Vịnh Hạ Long thu nhỏ của tỉnh Bắc Kạn và được người xưa phong tặng&nbsp;<strong>"Thiên nhiên đệ nhất Ba Hồ".&nbsp;</strong>Quý khách đi thuyền tham quan động Puông, Ao Tiên,… tìm hiểu đời sống của người dân tộc Tày, Nùng.</li><li class="ql-align-justify">Dừng chân tại&nbsp;<strong>Đảo Bà Góa,&nbsp;</strong>một hòn đảo nhỏ xinh xắn nằm ngay vị trí trong tâm của Hồ, gắn liền với sự tích hình thành của Hồ.&nbsp;</li></ul><p><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li>Đoàn khởi hành về Hà Nội.</li><li>Hướng dẫn viên tiễn đoàn ra&nbsp;<strong>sân bay Nội Bài</strong>&nbsp;đón chuyến bay về TP.HCM.</li><li>Kết thúc chuyến tham quan, chia tay đoàn và hẹn gặp lại.</li></ul><h1><br></h1><h1><br></h1><h1><br></h1><p><br></p>',13099000,10,10,'Hà Giang','Du lịch thông thường','Miền Bắc','6 ngày 5 đêm'),
(8,'Du lịch Phú Thọ - Đền Hùng','<p><strong>NGÀY 1 |</strong></p><p><strong>06h00:&nbsp;</strong>Xe ô tô và hướng dẫn viên Du Lịch Việt đón quý khách tại điểm hẹn khởi hành đi&nbsp;<strong>đền Hùng</strong>.</p><p><strong>09h30:&nbsp;</strong>Tới đền Hùng, quý khách sẽ được chiêm ngưỡng một quần thể di tích và lịch sử nổi tiếng của dân tộc ta. Nơi đây các vua Hùng đã xây dựng nước Văn Lang, quốc gia đầu tiên của dân tộc ta và cũng tại nơi đây quý khách sẽ tìm hiểu về các vua hùng, về các nàng công chúa Tiên Dung, Mị Nương, về hoàng tử Lang Liêu với sự tích bánh chưng, bánh dày….</p><p>Leo qua hơn 200 bậc đá, quý khách lần lượt thăm quan&nbsp;<strong>đền Hạ, đền Trung, đền Thượng, lăng vua Hùng</strong>&nbsp;và kết thúc tại&nbsp;<strong>đền Giếng.</strong></p><p><strong>11h30:</strong>&nbsp;Quý khách ăn trưa và nghỉ ngơi tại nhà hàng.</p><p><strong>13h00:</strong>&nbsp;Quý khách tham quan&nbsp;<strong>Bảo Tàng&nbsp;</strong>và&nbsp;<strong>Đền Mẫu Âu Cơ</strong>.</p><p><strong>16h00:</strong>&nbsp;Qúy khách lên xe, đoàn khởi hành về Hà Nội. Về đến Hà Nội, kết thúc chuyến đi, tạm chia tay và hẹn gặp lại!</p><h1><br></h1><p><br></p>',750000,0,10,'Phú Thọ','Du lịch thông thường','Miền Bắc','1 ngày'),
(9,'Du lịch Hà Nội - Đền Bà Chúa Kho - Chùa Dâu Bắc Ninh','<p><strong>NGÀY 1 |</strong></p><p><strong>Sáng:</strong>&nbsp;Xe ôtô và Hướng dẫn viên của Du lịch Việt đón Quý khách tại điểm hẹn trong thành phố khởi hành đi Đền Bà Chúa Kho.</p><p>Đến Đền Bà Chúa Kho, quý khách vào thăm quan và lễ cầu lộc, cầu Tài tại&nbsp;<strong>đền Bà Chúa Kho</strong>&nbsp;- nơi tưởng niệm một người phụ nữ Việt Nam đã khéo tổ chức sản xuất, tích trữ lương thực, trông nom kho tàng quốc gia trong thời kỳ trước và sau chiến thắng Như Nguyệt.</p><p>Sau đó Quý khách đến thăm&nbsp;<strong>Đền Đô</strong>&nbsp;- hay còn gọi là Đền Bát Ngàn, nơi thờ 8 vị Vua triều Lý, nghe HDV giới thiệu lịch sử huy hoàng về sự phát triển quốc gia độc lập (1010 - 1225).</p><p><strong>Trưa:</strong>&nbsp;Quý khách dùng bữa trưa, nghỉ ngơi tại nhà hàng.</p><p><strong>Chiều:</strong>&nbsp;Du khách khởi hành đến&nbsp;<strong>Chùa Dâu</strong>. Tại đây quý khách tham quan quần thể chùa với Hòa Phong 3 tầng cao khoảng 17m; trong tháp có chuông lớn đúc dưới triều Cảnh Thịnh (1793), khánh lớn bằng đồng đúc năm Minh Mạng 18. Ngoài ra Quý khách còn được chiêm ngưỡng hệ thống tượng Phật đồ sộ như: Tượng La Hán, tượng Bà Pháp Vân, tượng Kim Đồng Ngọc Nữ.</p><p>Xe đưa quý khách trở về Hà Nội, xe trả đoàn tại điểm tập trung ban đầu.</p><h1><br></h1><p><br></p>',700000,2,10,'Bắc Ninh','Du lịch thông thường','Miền Bắc','1 ngày'),
(10,'Du lịch Mộc Châu','<p><strong>NGÀY 1 |&nbsp;HÀ NỘI - MỘC CHÂU (Ăn trưa, tối)</strong></p><p class="ql-align-justify"><strong>06h30:&nbsp;</strong>Xe và hướng dẫn viên đón Quý khách tại điểm hẹn. Xe dừng chân trên đường tại khu vực Xuân Mai để Quý khách tự do ăn sáng.</p><p class="ql-align-justify"><strong>10h30:</strong>&nbsp;Dừng chân trên&nbsp;<strong>đèo Thung Khe</strong>&nbsp;để chụp ảnh và ngắm cảnh rừng núi hùng vỹ của Hòa Bình.</p><p class="ql-align-justify"><strong>12h30:&nbsp;</strong>Đến Mộc Châu, Quý khách ăn trưa tại nhà hàng, nghỉ ngơi.</p><p class="ql-align-justify"><strong>13h30:</strong>&nbsp;Hướng dẫn viên đưa Quý khách đi thăm quan các điểm sau:&nbsp;</p><ul><li class="ql-align-justify"><strong>Thác Dải Yếm</strong>&nbsp;- Một thác nước tuy nhỏ nhưng mang một vẻ đẹp quyến rũ. Tương truyền, dòng thác này là dải yếm của người con gái cứu chàng trai thoát khỏi dòng nước lũ.</li><li class="ql-align-justify">Trường hợp vào mùa khô, thác ít nước chúng tôi sẽ thay thế điểm thăm quan Thác Dải Yếm bằng thăm khu Tượng đài Tây tiến để thăm, nghe và tìm hiểu về Con đường Tây tiến năm xưa và tình hữu nghị Việt - Lào trong những năm kháng chiến.</li><li class="ql-align-justify"><strong>Vườn hoa Happy land</strong>&nbsp;- Happy land có diện tích gần 5ha, nằm giữa một thung lũng rộng lớn ở bản Lùn, xã Mường Sang. Đến đây, du khách sẽ được đón chào bởi những thảm hoa được trồng từ nhiều loại hoa khác nhau, trong đó có những loài hoa đặc trưng của vùng Tây Bắc).&nbsp;</li><li class="ql-align-justify"><strong>Rừng thông Bản Áng</strong>&nbsp;giống như một Đà Lạt thu nhỏ trên đất cao nguyên Mộc Châu. Tại đây Quý khách có thể thuê xe đạp hoặc ngồi xe ngựa kéo để dạo vòng vòng ngắm cảnh thư giãn xung quanh Hồ Bản Áng. Hoặc đơn giản là leo lên những đồi thông, ngồi nghỉ ngơi và lấy lại cân bằng sau những bộn bề cuộc sống nơi thành thị ồn ào.</li></ul><p><strong>18h00:</strong>&nbsp;Quý khách ăn tối tại nhà hàng sau đó về khách sạn nhận phòng nghỉ ngơi. Buổi tối Quý khách tự do khám phá cao nguyên Mộc Châu. Nghỉ đêm Mộc Châu.</p><p><strong>NGÀY 2 |&nbsp;MỘC CHÂU - MAI CHÂU - HÀ NỘI (Ăn sáng, trưa)</strong></p><p class="ql-align-justify"><strong>06h30:</strong>&nbsp;Sau bữa sáng tại nhà hàng hoặc khách sạn, Quý khách cùng hướng dẫn viên tiếp tục thăm quan Đồi chè Mộc Sương và chụp hình cùng với&nbsp;<strong>đồi chè Trái tim</strong>, có lẽ đồi chè Mộc Sương không chỉ là đồi chè đẹp nhất của Cao nguyên Mộc Châu mà còn là đồi chè đẹp nhất của Việt Nam bởi những luống chè xanh mởn tròn trịa và uốn lượn xa ngút tầm mắt đưa đến một cảm giác lãng mạn và bình yên. Đây cúng chính là vùng nguyên liệu để sản xuất ra những loại chè ngon và nổi tiếng mà một trong những thương hiệu nổi tiếng chính là Ô Long trà.</p><p class="ql-align-justify"><strong>09h30:</strong>&nbsp;Quý khách lên xe về Hà Nội. Trên đường tiếp tục dừng chụp hình tại những điểm đẹp mà đoàn chưa kịp chụp hình ngày hôm trước.</p><p class="ql-align-justify"><strong>11h30:</strong>&nbsp;Ăn trưa tại Mai Châu. Sau bữa trưa, Quý khách có thể&nbsp;<strong>khám phá thung lũng Mai Châu</strong>&nbsp;bằng xe đạp (hoặc xe điện, chi phí thuê xe đạp hoặc xe điện tự túc). Quý khách thăm những ngôi nhà sàn nhỏ xinh của người Thái để tìm hiểu những nét văn hóa độc đáo của họ, thăm những gian hàng thổ cẩm, cùng học cách dệt thổ cẩm của người Thái và mua những món quà lưu niệm nơi đây. Sau đó Quý khách tự do đạp xe hoặc dạo bộ và mua sắm đồ, chụp hình.</p><p class="ql-align-justify"><strong>15h00:&nbsp;</strong>Quý khách lên xe về Hà Nội. Trên đường về ghé dừng nghỉ ngơi tại đặc sản sữa Ba Vì mua đồ về làm quà cho bạn bè và người.</p><p class="ql-align-justify"><strong>19h00:</strong>&nbsp;Xe đưa quý khách tới Hà Nội. Kết thúc chương trình. Hẹn gặp lại Quý khách!</p><h1><br></h1><p><br></p>',2399000,3,10,'Sơn La','Du lịch thông thường','Miền Bắc','2 ngày 1 đêm'),
(11,'Du Lịch Buôn Ma Thuột','<p><strong>NGÀY 1 |&nbsp;TP.HÀ NỘI- BUÔN MÊ THUỘT – HỒ LĂK</strong></p><p>​Buổi chiều, Quý khách ra sân bay làm thủ tục đón chuyến bay đi Buôn Mê Thuột. Đến nơi xe đón đoàn về trung tâm thành phố, Quý khách có thể tự do dạo chợ hoặc thưởng thức cà phê phố núi chờ xe và đoàn từ thành phố lên đón và đưa Quý khách tiếp tục đi Hồ Lăk. Đến nơi, Quý khách nhận phòng nghỉ ngơi.</p><p>​Buổi tối, nghỉ ngơi tự do hoặc Du khách có thể tham dự đêm lửa trại, uống rượu cần, văn nghệ cồng chiêng, giao lưu văn hóa với đồng bào&nbsp;dân tộc Tây Nguyên (chi phí tự túc). Quý khách nghỉ đêm ở hồ Lăk hoặc ở Ban Mê Thuột.</p><p><strong>NGÀY 2 |&nbsp;BUÔN MÊ THUỘT</strong></p><p>Buổi sáng: Quí khách tham quan và trải nghiệm những điểm đặc sắc ở Buôn Jun như cưỡi voi, đi thuyền độc mộc, ngắm bình minh trên hồ, chiêm ngưỡng cảnh đẹp nơi miền sơn cước. Sau đó đoàn về thành phố Buôn Mê Thuột thăm Buôn Akô Dhong, nơi cư trú của đồng bào Eđê với những căn nhà sàn truyền thống. Đoàn tiếp tục viếng chùa Khải Đoan, tham quan Bảo tàng Dân Tộc, tìm hiểu đời sống vật chất, tinh thần của cộng đồng các dân tộc Tây Nguyên.</p><p>Buổi chiều, Du khách khởi hành đi Buôn Đôn, tham quan cầu treo và ngắm cảnh sông Sêrêpôk, nhà sàn cổ 120 năm của người Lào, tham quan mộ và nghe kể chuyện về Vua săn voi; vào khu du lịch sinh thái Bản Đôn, bắt đầu chương trình dã ngoại, du khách có thể đi bộ hoặc đạp xe ngắm cảnh rừng quốc gia Yok Đôn, cưỡi voi xuyên rừng và thăm buôn làng.&nbsp;</p><p>Buổi tối, du khách đi dạo Thành phố về đêm, thưởng thức hương vị cà phê phố núi, nghỉ đêm tại Buôn Mê Thuột hoặc Hồ Lak.</p><p><strong>NGÀY 3 |&nbsp;BUÔN MÊ THUỘT- TP.HÀ NỘI</strong></p><p>Buổi sáng, trả phòng, xe đưa đoàn ra chợ chợ Buôn Mê Thuột, Quý khách mua đặc sản. Sau đó đoàn khởi hành ra sân bay Buôn Mê Thuột làm thủ tục đón chuyến bay về lại thành phố. Đến Hà Nội chia tay tạm biệt, hẹn tái ngộ cùng Quý khách.</p><p><br></p><p><br></p>',1970000,20,10,'Buôn Mê Thuộc','Du lịch thông thường','Miền Trung','3 ngày 2 đêm'),
(12,'Du lịch Miền Trung - Đảo Bình Hưng - Ninh Chữ - Vườn Nho','<p><strong>NGÀY 1 |&nbsp;CAM RANH – ĐẢO BÌNH HƯNG – NINH CHỮ (Ăn sáng, trưa, chiều) (Xem thêm)</strong></p><p class="ql-align-justify"><strong>Sáng</strong>: Đến Cam Ranh. Đoàn khởi hành đến bãi Kinh -Bình Tiên, đón tàu đến đảo Bình Hưng.</p><ul><li class="ql-align-justify">Tham quan bãi Nước Ngọt – bãi tắm, quý khách có thể tắm suối nước ngọt vừa có thể tắm biển tại đây.</li><li class="ql-align-justify">Quý khách tự do tắm biển, lặn ngắm san hô – với làn nước trong veo, quang cảnh xung quanh thơ mộng, sở hữu những rặng san hô tuyệt đẹp cùng mực nước biển thấp và màu nước trong xanh.</li></ul><p class="ql-align-justify"><strong>Trưa:&nbsp;</strong>Dùng cơm trưa.</p><ul><li class="ql-align-justify">Đoàn đi tham quan một vòng trên đảo, viếng thăm chùa Bình Hưng, lăng Nam Hải, ngọn Hải Đăng Hòn Chút nơi mà khi đặt chân lên tại đây quý khách chúng ta có thể thấy rỏ được toàn cảnh bình hưng và biển đông.</li><li class="ql-align-justify">Quý khách tự do tắm biển.</li><li class="ql-align-justify">Đoàn đón chuyến tàu trở về đất liền, khởi hành đến Ninh Chữ</li></ul><p><strong>Tối</strong>: Dùng cơm tối. Nghỉ đêm tại Ninh Chữ.</p><p><strong>NGÀY 2 |&nbsp;NINH CHỮ – TP.HCM (Ăn sáng, trưa) (Xem thêm)</strong></p><p class="ql-align-justify"><strong>Sáng</strong>: Dùng bữa sáng.&nbsp;Đoàn tham quan vườn nho ông Ba Mọi ăn nho, uống rượu nho miễn phí. Vườn nho có tới 7 loại nho, trong đó 4 loại là nho tươi ăn quả, 3 loại được dùng để làm rượu. Hướng dẫn viên hướng dẫn cho du khách quy trình chăm sóc đến các công đoạn thu hoạch, chế biến rượu nho.</p><p class="ql-align-justify"><strong>Trưa</strong>: Đoàn dùng cơm trưa. Khởi hành về TP.HCM, kết thúc chương trình tour, chia tay và hẹn gặp lại!</p><p><br></p><p><br></p>',1980000,0,10,'Ninh Thuận','Du lịch thông thường','Miền Trung','2 ngày 2 đêm'),
(13,'Du lịch Tây Ninh - Tòa Thánh Cao Đài - Vinpearl - Núi Bà Đen - Tu Viện Khánh An','<p><strong>NGÀY 1 |&nbsp;TP. HCM – TÂY NINH - VINPEARL (Ăn sáng, trưa, chiều)</strong></p><p class="ql-align-justify"><strong>Sáng&nbsp;&nbsp;&nbsp;Xe và Hướng Dẫn Viên Công Ty Du Lịch Việt đón Quý khách tại điểm hẹn.</strong></p><ul><li class="ql-align-justify">Khởi hành đi Tây Ninh. Quý khách dùng bữa sáng</li><li class="ql-align-justify">Đoàn ghé Tòa Thánh Cao Đài, tham quan tìm hiểu tôn giáo độc đáo của Đạo Cao Đài, du khách có thể chiêm ngưỡng toàn bộ nghi thức trang trọng của đạo hữu Cao Đài. Với kiến trúc độc đáo, thể hiện một sự dung hòa của nhiều yếu tố tâm linh từ Đông sang Tây, Tòa Thánh Tây Ninh không chỉ là nơi hành hương của các tín đồ đạo Cao Đài mà còn thu hút một lượng lớn du khách trong và ngoài nước tới tham quan mỗi ngày.</li></ul><p><strong>Trưa&nbsp;&nbsp;&nbsp;Đoàn dùng cơm trưa với những đặc sản của vùng đất Tây Ninh.&nbsp;&nbsp;</strong>&nbsp;&nbsp;</p><ul><li>Đoàn làm thủ tục nhận phòng tại Vinpearl Hotel Tây Ninh 5* - khách sạn 5* đầu tiên và duy nhất tại Tây Ninh với 21 tầng và tầm nhìn bao quát toàn thành phố.&nbsp;</li><li>Qúy khách tự do nghỉ ngơi, tắm hồ bơi trong lồng kính, bể sục Jacuzzi và những tiện ích đẳng cấp 5* của Vinpearl. Trải nghiệm ngắm hoàng hôn từ hồ bơi sân thượng, thưởng thức một ly cocktail từ quầy bar và lưu lại những tấm hình sang chảnh để khoe cùng bạn bè.</li></ul><p><strong>Chiều&nbsp;Dùng cơm chiều.</strong></p><ul><li>Nghỉ đêm tại Vinpearl Hotel. Quý khách tự do khám phá thành phố Tây Ninh về đêm.</li></ul><p><strong>NGÀY 2 |&nbsp;NÚI BÀ ĐEN – TU VIỆN KHÁNH AN – TP. HCM (Ăn sáng, trưa)</strong></p><p class="ql-align-justify">Sáng Dùng điểm tâm sáng tại khách sạn.</p><ul><li class="ql-align-justify">Quý khách có thể tự do nghỉ ngơi, sử dụng tiện ích của khách sạn hoặc tham quan Núi Bà Đen.</li><li class="ql-align-justify">Đoàn đến Núi Bà Đen - nổi tiếng về cảnh sắc thiên nhiên hoang sơ và quần thể kiến trúc tâm linh lâu đời; ngọn núi với chiều cao hơn 986m được mệnh danh là “nóc nhà” của khu vực Nam Bộ. Quý khách trải nghiệm hệ thống cáp treo hiện đại Sun World Baden Moutain với 2 tuyến cáp treo hiện đại trong hành trình chiêm bái và chinh phục ngọn núi cao nhất vùng Đông Nam Bộ của du khách thập phương trở nên dễ dàng hơn bao giờ hết. (chi phí cáp treo tự túc)</li><li class="ql-align-justify">Tuyến cáp treo Chùa Hang đưa du khách đến Quần Thể Tâm Linh Chùa Bà: ở độ cao 350 mét giữa lưng chừng núi là quần thể kiến trúc hang động, chùa chiền mang nét đẹp thiên phú và nhân tạo với nhiều truyền thuyết ly kỳ, bí ẩn gồm Chùa Bà, Chùa Hang, Động Hoàng Chung, Chùa Trung, Chùa Mới… Những ngôi chùa cổ và di tích lịch sử linh thiêng tại Núi Bà đã được công nhận là Di tích lịch sử văn hóa và danh lam thắng cảnh cấp quốc gia. Ngoài ra, Quý khách còn có thể trải nghiệm máng trượt Chùa Bà – một trong những hệ thống máng trượt độc đáo, mới lạ và nổi tiếng được lắp đặt trên sườn núi đầu tiên ở Việt Nam.</li><li class="ql-align-justify">Tuyến cáp treo Vân Sơn đưa du khách xuyên mây hòa mình vào cảnh vật thiên nhiên hùng vĩ để chinh phục ngọn núi cao nhất vùng Đông Nam Bộ. Hành trình lên đỉnh núi thiêng sẽ mang đến cho du khách những trải nghiệm tuyệt vời với tầm nhìn 360 độ ngắm trọn Tây Ninh, chạm tay vào cột mốc 986 mét, ngắm trời xanh, núi biếc, hồ Dầu Tiếng xinh đẹp và vùng đồng bằng trù phú đẹp như tranh…</li></ul><p class="ql-align-justify">Trưa Dùng cơm trưa.</p><ul><li class="ql-align-justify">Khởi hành về TP.HCM.&nbsp;</li><li class="ql-align-justify">Về tới TP.HCM, kết thúc hành trình tour. Chào tạm biệt và hẹn gặp lại.</li></ul><p><br></p>',3090000,0,10,'Tây Ninh','Du lịch thông thường','Miền Nam','2 ngày 1 đêm'),
(14,'Du lịch Long An','<p><strong>NGÀY 1 |&nbsp;Công Viên Kỳ Quan Thế Giới - Làng Nổi Tân Lập - Con Đường Xuyên Rừng Tràm Đẹp Nhất Việt Nam</strong></p><p class="ql-align-justify"><strong>Sáng</strong>: Xe và Hướng Dẫn Viên&nbsp;<strong>Công Ty Du Lịch Việt</strong>&nbsp;đón Quý khách tại điểm hẹn.</p><p class="ql-align-justify">Khởi hành đi&nbsp;<strong>Long An,&nbsp;</strong>trên đường Quý khách sẽ nghe thuyết minh về những địa danh nổi tiếng của vùng đất Long An như rượu đế Gò Đen, gạo nàng thơm Chợ Đào, Bến Lức, …</p><p class="ql-align-justify">Quý khách dừng chân dùng bữa sáng&nbsp;<em>(chi phí tự túc)</em></p><p class="ql-align-justify">Đoàn ghé thăm&nbsp;<strong>Thiền Viện Trúc Lâm Chánh Giác&nbsp;</strong>– nằm sâu trong vùng trũng của xã Thanh Tân, Tân Phước Tiền Giang, được xem là một trong những thiền viện lớn nhất cả nước rộng hơn 30ha. Là công trình kiến trúc đầu tiên ở Việt Nam với 4 Thánh tích Phật giáo có nguồn gốc từ ấn Độ và Nepal với 25 hạng mục, đặc biệt là bức tượng Phật Thích Ca Mâu Ni được tạc bằng đá ngọc, trên tay cầm hoa sen, cao 4.5m, năng 30 tấn do các nghệ nhân Myanmar chế tác.</p><p class="ql-align-justify">Đến&nbsp;<strong>Làng Nổi Tân Lập</strong>&nbsp;– được xem là một trong những điểm du lịch sinh thái mới nhất tại&nbsp;<strong>Long An</strong>, với hệ thống rừng tràm nguyên sinh với những đầm hoa súng tạo nên phong cảnh tuyệt đẹp. Quý khách sẽ khám phá con đường bê tông xuyên rừng tràm, giữa những con đường là những đầm hoa súng, đầm sen khoe sắc, những bãi bèo tai chuột xanh mướt điểm xuyết đóa rong vàng li ti, thỉnh thoảng nghe âm thanh vui tai của tiếng chim hót đặc trưng của vùng này như cò, bìm bịp, cồng cộc,… Đặc biệt Quý khách còn có thể trải nghiệm ngắm nhìn toàn cảnh rừng tràm từ trên những đài quan sát trên cao phòng tầm mắt ra xa với bạt ngàn là màu xanh bao la hung vĩ.</p><p class="ql-align-justify"><strong>Trưa:&nbsp;</strong>Quý khách dùng bữa trưa<strong>&nbsp;</strong>với các đặc sản độc đáo của vùng nước nổi.</p><p class="ql-align-justify">Đoàn ngồi xuồng ba lá len lỏi vào rừng tràm, tận hưởng cảm giác lênh đênh trên sông nước, thưởng ngoạn không khí yên bình nơi đây.</p><p class="ql-align-justify">Đoàn khởi hành về TP.HCM, trên đường ghé<strong>&nbsp;Công Viên Kỳ Quan Thế Giới</strong>&nbsp;– được giới trẻ đặt cho là điểm “check-in vàng” vì ở tập trung những công trình kiến trúc nổi tiếng thế giới được thu nhỏ tinh xảo như thật. Quý khách sẽ có những trải nghiệm chụp hình các bức ảnh rất độc đáo tại đây.</p><p class="ql-align-justify">Đoàn khởi hành về TP.HCM. Kết thúc chuyến đi, chia tay đoàn và hẹn gặp lại Quý khách.</p><p><br></p>',700000,10,10,'Long An','Du lịch thông thường','Miền Nam','1 ngày'),
(15,'Du lịch Phú Quốc','<p><strong>NGÀY 1 |&nbsp;TP.HCM – PHÚ QUỐC (ăn trưa, chiều)</strong></p><p class="ql-align-justify">Sáng:&nbsp;Quý khách có mặt tại ga quốc nội, sân bay Tân Sơn Nhất trước giờ bay ít nhất ba tiếng.</p><p class="ql-align-justify">Đại diện&nbsp;<strong>công ty Du Lịch Việt</strong>&nbsp;đón và hỗ trợ Quý khách làm thủ tục đón chuyến bay đi<strong>&nbsp;Phú Quốc.</strong></p><p class="ql-align-justify">Đến&nbsp;<strong>sân bay Phú Quốc</strong>, Hướng dẫn viên đón Quý khách Tham quan&nbsp;<strong>Dinh Cậu</strong>.</p><p class="ql-align-justify">Tham quan các làng nghề truyền thống nổi tiếng trên đảo:</p><ul><li class="ql-align-justify"><strong>Hồ tiêu Phú Quốc</strong>: tìm hiểu về cách trồng tiêu tại vườn. Tiêu Phú Quốc nổi tiếng với hạt to, đen và cay thơm. Du khách có thể mua tiêu về làm quà tại nhà vườn.</li></ul><p><br></p><p><em>Hồ tiêu Phú Quốc</em></p><p>&nbsp;</p><ul><li class="ql-align-justify"><strong>Rượu sim</strong>: loại rượu vang làm từ trái sim rừng chín.Gặp gỡ và trò chuyện với người đầu tiên chế biến ra rượu sim sơn tại cơ sở sản xuất rượu sim Bảy Gáo.</li></ul><p><br></p><p><em>Rượu sim</em></p><p>&nbsp;</p><ul><li class="ql-align-justify"><strong>Nhà thùng nước mắm</strong>: Tìm hiểu cách ủ và chế biến nước mắm cá cơm rất nổi tiếng trong và ngoài nước theo cách làm truyền thống của người dân Phú Quốc với hàm lượng dinh dưỡng rất cao.</li></ul><p><br></p><p><em>Nhà thùng nước mắm</em></p><p class="ql-align-justify"><strong>Trưa:&nbsp;</strong>Dùng cơm trưa.</p><p class="ql-align-justify">Khám phá<strong>&nbsp;</strong>thành phố không ngủ<strong>&nbsp;Grand World Phú Quốc –&nbsp;</strong>checkin dòng sông<strong>&nbsp;Venice</strong></p><p class="ql-align-justify"><strong>(Chi phí tự túc, không bao gồm: vé tham quan)</strong></p><p class="ql-align-justify">Khám Phá&nbsp;<strong>Phú Quốc UnitedCenter - Grandword&nbsp;</strong>,Trung tâm mới sầm uất và đa tiện tích của Phú Quốc với hệ sinh thái dịch vụ trọn vẹn từ dân sinh đến giải trí, nay được hoàn thiện ngoạn mục bằng sự ra mắt của Grand World, Thiên đường giải trí - mua sắm - lễ hội độc đáo và đa dạng bậc nhất Đông Nam Á với chuỗi trải nghiệm xuyên suốt 24h.</p><p class="ql-align-justify"><strong>Quý Khách được Checkin sống ảo Miễn phí tại đây .</strong></p><p class="ql-align-justify"><strong>SIÊU QUẦN THỂ NGHỈ DƯỠNG - KHÁM PHÁ - MUA SẮM - GIẢI TRÍ KHÔNG NGỦ HÀNG ĐẦU KHU VỰC - PHÚ QUỐC UNITED CENTER</strong></p><p class="ql-align-justify"><strong>Tối:&nbsp;</strong>Dùng cơm tối.</p><p class="ql-align-justify">Nghỉ đêm tại Phú Quốc.</p><p><strong>NGÀY 2 |&nbsp;PHÚ QUỐC – BÃI SAO – CÂU CÁ – SUNSET SANATO (Ăn sáng, trưa, tối)</strong></p><p class="ql-align-justify">Sáng:&nbsp;Dùng bữa sáng.</p><p class="ql-align-justify">Xe đưa đoàn tiến về phía Nam, lên tàu câu cá.</p><ul><li class="ql-align-justify"><strong>Ngọc trai cao cấp Phú Quốc</strong>: quý khách được trực tiếp tham quan quy trình nuôi trai lấy ngọc tại các cơ sở nuôi cấy ngọc trai chuyên nghiệp tại Phú Quốc.</li></ul><p class="ql-align-justify">&nbsp;</p><p><br></p><p><em>Ngọc trai cao cấp Phú Quốc</em></p><p>&nbsp;</p><ul><li class="ql-align-justify"><strong>Trên tàu:</strong>&nbsp;Với đội ngủ thủy thủ đoàn &amp; HDV địa phương giàu kinh nghiệm, quý khách sẽ tận hưởng từng khoảnh khắc đáng nhớ khi chính tay mình buông câu bắt được những chú cá bồng mú phàm ăn trong các rạn san hô tại hòn Dừa, hòn Rỏi, ….</li></ul><p class="ql-align-justify"><strong>Trưa:</strong>&nbsp;Dùng cơm trưa.</p><ul><li class="ql-align-justify"><strong>Bãi Sao</strong>: quý khách tiếp tục dừng chân tại một trong những bãi biển đẹp nhất Phú Quốc với bãi cát dài thoang thoảng, trắng mịn. Đoàn tự do chụp hình lưu niệm.</li><li class="ql-align-justify"><strong>Nhà tù Phú Quốc</strong>: khu di tích lịch sử - nơi chứng kiến bao tội ác của thực dân Pháp và đế quốc Mỹ khi giam cầm hơn 32 ngàn tù binh chiến tranh (có lúc lên&nbsp;&nbsp;đến con số 40 ngàn tù binh) trong khoảng thời gian tồn tại chưa đầy 6 năm.</li></ul><p><br></p><p><em>Nhà tù Phú Quốc</em></p><p>&nbsp;</p><p class="ql-align-justify">Đoàn tham quan&nbsp;<strong>Sunset Sanato Beach Club</strong>, nơi được mệnh danh là&nbsp;<strong>“Điểm ngắm hoàng hôn đẹp nhất đảo Ngọc”.</strong>&nbsp;tọa lạc ngay trung tâm Bãi Trường. Không gian biển ở Sunset Sanato Beach Club rất thoáng tầm nhìn và được điểm tô bằng nhiều công trình kiến trúc cực độc đáo, mang dấu ấn khó phai. Và có lẽ chính những khối kiến trúc kì lạ và đầy sáng tạo ấy&nbsp;đã giúp cho Sunset Sanato Beach Club trở thành điểm check-in siêu độc mà ai đến Phú Quốc cũng phải "đỏm dáng"&nbsp;làm ngay vài kiểu.</p><p class="ql-align-justify"><strong>Tối:</strong>&nbsp;&nbsp;&nbsp;&nbsp;Dùng cơm tối.</p><p class="ql-align-justify">Nghỉ đêm tại Phú Quốc.</p><p><strong>NGÀY 3 |&nbsp;PHÚ QUỐC – TP.HCM (Ăn sáng) (Ăn trưa tự túc)</strong></p><p class="ql-align-justify">Sáng: Dùng bữa sáng.&nbsp;</p><p class="ql-align-justify">Quý khách có thể tự do tắm biển, nghỉ ngơi tại khách sạn hoặc Quý khách có thể chọn một trong các điểm tham quan tham khảo như sau:</p><p class="ql-align-justify"><strong>*<u>Lựa chọn 1</u></strong>: Khám phá&nbsp;<strong>CÔNG VIÊN CHỦ ĐỀ VINWONDERS PHÚ QUỐC</strong></p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Chi phí tự túc; không bao gồm: phương tiện vận chuyển, vé tham quan Vinwonders + 01 bữa ăn trưa)</strong></p><p class="ql-align-justify"><strong>VinWonders Phú Quốc&nbsp;</strong>– Công viên với 06 chủ đề khác nhau, được kết nối bằng câu chuyện chủ đề xuyên suốt:</p><p class="ql-align-justify">-&nbsp;<strong>Khu lâu đài trung tâm – Châu Âu trung cổ</strong></p><p class="ql-align-justify">-&nbsp;<strong>Khu Viking – Khu làng bí mật</strong></p><p class="ql-align-justify">-&nbsp;<strong>Khu cảm giác mạnh – thế giới phiêu lưu</strong></p><p class="ql-align-justify">-&nbsp;<strong>Khu công viên nước – thế giới lốc xoáy</strong></p><p class="ql-align-justify">-&nbsp;<strong>Khu cổ tích – Thế giới diệu kỳ</strong></p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Khu Thủy cung - Cung điện Hải Dương&nbsp;</strong></p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*<u>Lựa chọn 2</u></strong>: Khám phá&nbsp;<strong>CÔNG VIÊN CHĂM SÓC &amp; BẢO TỒN ĐỘNG VẬT VINPEARL SAFARI</strong></p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Chi phí tự túc; không bao gồm: phương tiện vận chuyển, vé tham quan Safari + 01 bữa ăn trưa)</strong></p><p class="ql-align-justify">Công viên chăm sóc và bảo tồn động vật Vinpearl Safari – vườn thú mở lớn nhất Việt Nam hiện nay, sẽ chính thức mở cửa đón khách tham quan vào ngày 24/12/2015 tại đảo ngọc Phú Quốc.</p><p class="ql-align-justify"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Công viên động vật bán hoang dã với Khu vườn thú mở:</em></strong></p><p class="ql-align-justify">Nơi bảo tồn hơn 100 loài động vật quý hiếm từ khắp nơi trên thế giới. Tại đây, các loài được chăm sóc và phát triển khỏe mạnh trong không gian chuồng trại rộng lớn, thoáng đãng và hài hòa với môi trường tự nhiên. Trải nghiệm Rừng linh trưởng, Vương quốc sư tử, Khu động vật có vú, Khu rừng của voi, Thế giới bò sát, Đầm lầy hà mã, …</p><p class="ql-align-justify"><strong><em>Các hoạt động trải nghiệm đời sống hoang dã:</em></strong></p><p class="ql-align-justify"><strong><em>+ Xem chương trình biểu diễn động vật:</em></strong></p><p class="ql-align-justify"><strong><em>+ Chụp ảnh với động vật</em></strong></p><p class="ql-align-justify"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;+ Cho động vật ăn</em></strong></p><p class="ql-align-justify">Ngoài ra, còn hệ thống nhà hàng với phong cách ẩm thực đa dạng và phố mua sắm với những món quà lưu niệm đặc sắc.</p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*<u>Lựa chọn 3</u>:&nbsp;</strong>Trải nghiệm&nbsp;<strong>Sun World Hon Thom Nature Park</strong>&nbsp;– Cáp treo ba dây vượt biển dài nhất&nbsp;<strong>Thế Giới.</strong></p><p class="ql-align-justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Chi phí tự túc; không bao gồm: phương tiện vận chuyển, vé cáp treo + 01 bữa ăn trưa)</strong></p><p class="ql-align-justify">Quý khách đến ga An Thới – Nhà ga đi Cáp treo Hòn Thơm. Quý khách trải nghiệm với cáp treo vượt biển dài nhất thế giớ với tổng chiều dài vượt biển 7.899,9m - cáp treo dài nhất thế giới được tổ chức Guinness chứng nhận.&nbsp;<em>(chi phí cáp treo và các dịch vụ vui chơi giải trí tại Hòn Thơm quý khách vui lòng tự túc).</em></p><p class="ql-align-justify">Khám phá đảo Hòn Thơm với vẻ đẹp hoang sơ, trong lành; hòa mình vào làn nước biển xanh biếc tại một trong những hòn đảo đẹp nhất Việt Nam; hào hứng với hàng loạt trò chơi biển: dù kéo, lái cano, chèo thuyền Kayak,...; không thể bỏ lỡ trải nghiệm thưởng thức hải sản tươi ngon, đặc sản Phú Quốc tại các nhà chòi bên bờ biển thơ mộng…</p><p class="ql-align-justify">Đoàn tự do đi chợ mua sắm đặc sản về làm quà cho người thân: ghẹ, hải mã sống ngâm rượu, hải mã chiên giòn, quả dứa biển và trái mỏ quạ để ngâm rượu thuốc…. những đặc sản chỉ có ở Phú Quốc&nbsp;<em>(chi phí tự túc).</em></p><p class="ql-align-justify">Hướng dẫn viên tiễn đoàn ra&nbsp;<strong>sân bay Phú Quốc</strong>, đón chuyến bay về TP.HCM.</p><p class="ql-align-justify">Kết thúc chương trình tham quan, chia tay và hẹn gặp lại.</p><h1><br></h1><p><br></p>',4399000,0,10,'Phú Quốc','Du lịch thông thường','Miền Nam','3 ngày 2 đêm'),
(16,'Du lịch Cà Mau - Bạc Liêu - Sóc Trăng','<p><strong>NGÀY 1 |&nbsp;CÀ MAU – NĂM CĂN – ĐẤT MŨI – BẠC LIÊU (Ăn sáng, trưa, chiều) (Xem thêm)</strong></p><p class="ql-align-justify"><strong>Sáng:&nbsp;</strong>Dùng bữa sáng tại nhà hàng.</p><ul><li class="ql-align-justify">Khởi hành về Năm Căn – thị trấn Cực Nam giàu có nguồn tài nguyên thủy hải sản.</li><li class="ql-align-justify">Xe đưa Quý Khách tới Đất Mũi (Mũi Cà Mau):</li><li class="ql-align-justify">Trên đường Quý khách ngắm nhìn Tượng đài Anh hùng - Liệt sỹ Phan Ngọc Hiển, người có công lãnh đạo khởi nghĩa Hòn Khoai (Cà Mau).</li><li class="ql-align-justify">Tham quan mốc tọa độ quốc gia.&nbsp;</li><li class="ql-align-justify">Panô biểu tượng mũi Cà Mau.&nbsp;</li><li class="ql-align-justify">Lên Vọng Lâm Đài cao trên 20m để ngắm nhìn tòan cảnh Mũi Cà Mau.&nbsp;</li><li class="ql-align-justify">Tham quan mô hình thu nhỏ Làng Rừng kháng chiến.&nbsp;</li></ul><p><strong>Trưa:&nbsp;</strong>Dùng cơm trưa. Đoàn trở về thị trấn Năm Căn, đến thăm, mua sắm tại chợ Năm Căn.</p><p><strong>Chiều:&nbsp;</strong>Sau khi dùng cơm chiều du khách có thời gian tự do nghỉ ngơi và khám phá thành phố về đêm, cảm nhận không gian thanh bình tự do đúng chất của chuyến du lịch. Nghỉ đêm khách sạn tại&nbsp;<strong>Cà Mau</strong>.</p><p><strong>NGÀY 2 |&nbsp;CÀ MAU – BẠC LIÊU – SÓC TRĂNG – TP.HCM (Ăn sáng, trưa) (Xem thêm)</strong></p><p class="ql-align-justify"><strong>Sáng:</strong>&nbsp;Dùng bữa sáng tại nhà hàng.</p><ul><li class="ql-align-justify">Khởi hành đến Bạc Liêu, Đoàn đến viếng thăm Nhà thờ Tắc Sậy (Nhà thờ Cha Trương Bửu Diệp).</li><li class="ql-align-justify">Tham quan Nhà công tử Bạc Liêu gắn liền với giai thoại các chàng công tử Bạc Liêu nổi tiếng vùng Lục tỉnh Nam Kỳ xa xưa.</li><li class="ql-align-justify">Tham quan Khu lưu niệm nhạc sĩ Cao Văn Lầu nơi an nghỉ cuối cùng của tác giả bài Dạ Cổ Hoài Lang nổi tiếng Nam Bộ. Tại đây du khách được nghe giới thiệu về thân thế, sự nghiệp của ông, quá trình phát triển nghệ thuật từ bản Dạ Cổ.</li><li class="ql-align-justify">Đến Sóc Trăng, Quý Khách viếng Chùa Mã Tộc (Chùa Dơi) – nơi tập trung hàng ngàn con dơi tự nhiên, chùa Đất Sét (Bửu Sơn Tự) với&nbsp;hàng trăm tượng lớn nhỏ đều được nặn từ đất sét, độc đáo hơn nơi đây còn có 8 cây nến nặng tổng cộng 1,4 tấn sáp.</li></ul><p class="ql-align-justify"><strong>Trưa:</strong>&nbsp;Dùng cơm trưa. Đoàn khởi hành về TP.HCM, trên đường Quý khách có dịp chiêm ngưỡng vẻ đẹp cầu Cần Thơ nằm trên quốc lộ 1A nối liền hai bờ sông Hậu thuộc tỉnh Vĩnh Long và thành phố Cần Thơ. Về đến TP.HCM, đưa Quý khách về điểm đón ban đầu. Chia tay Quý khách và hẹn gặp lại.</p><p><br></p><h1><br></h1><p><br></p>',2590000,30,10,'Miền Tây','Du lịch thông thường','Miền Nam','2 ngày 2 đêm'),
(17,'Du Lịch Vũng Tàu','<p><strong>NGÀY 1 |&nbsp;TP.Hồ Chí Minh – Bình Châu – Hồ Cốc – Vũng Tàu</strong></p><p>05:00&nbsp;Xe đón đoàn tại Du lịch Việt khởi hành đi Vũng Tàu. Dừng chân dung điểm tâm.</p><p>09:00&nbsp;Đến KDL Bình Châu – Tham quan, tự do ngâm chân, tắm bùn…(chi phí tự túc)</p><p>12:00&nbsp;Ăn trưa tại biển Hồ Cốc.</p><p>13:00&nbsp;Tiếp tục hành trình đi Vũng Tàu. Đến khách sạn nhận phòng. Tự do tắm biển, thưởng thức các món đặc sản: bánh khọt Cây Vú Sữa, bánh bông lan trứng muối..</p><p>18h00&nbsp;Ăn chiều. Tự do dạo biển về đêm hoặc nghỉ ngơi tại khách sạn</p><p><strong>NGÀY 2 |&nbsp;Vũng Tàu – Long Sơn – TP.Hồ Chí Minh</strong></p><p>06:30&nbsp;Dùng buffet sáng tại khách sạn.</p><p>08:00&nbsp;Đoàn khởi hành tham quan Bạch Dinh là một dinh thự có kiến trúc châu Âu cuối thế kỷ 19, Nơi đây từng được dùng làm nơi nghỉ mát cho Toàn quyền Đông Dương, Hoàng đế Bảo Đại và các đời Tổng thống Việt Nam Cộng hòa. .</p><p>Sau đó, quý khách tham quan Tượng Chúa Dang Tay ngắm nhìn toàn cảnh Vũng Tàu từ một trong những vị trí đẹp nhất thành phố hiện giờ.</p><p>11:00&nbsp;&nbsp;Làm thủ tục trả phòng. Ăn trưa – Khởi hành về TP.HCM</p><p>Dừng chân mua sắm đặc sản tại khu Bò Sữa Long Thành.</p><p>&nbsp;</p><p>17:30&nbsp;Về lại Du Lịch Việt. Chia tay đoàn, hẹn gặp lại quý khách trên những hành trình tham quan khác.</p><p><br></p><h1><br></h1><p><br></p>',1400000,0,10,'Vũng Tàu','Du lịch biển','Miền Nam','2 ngày 1 đêm'),
(18,'Du lịch Đồng Tháp Làng Hoa Sa Đéc - Vườn Quýt Hồng Lai Vung','<p><strong>NGÀY 1 |</strong></p><p><strong>Sáng:&nbsp;</strong>Xe và Hướng Dẫn Viên&nbsp;<strong>Công Ty Du Lịch Việt</strong>&nbsp;đón Quý khách tại điểm hẹn.</p><ul><li>Khởi hành đi&nbsp;<strong>Đồng Tháp,&nbsp;</strong>trên đường Quý khách sẽ nghe thuyết minh về những địa danh nổi tiếng của vùng đất Long An như rượu đế Gò Đen, gạo nàng thơm Chợ Đào, Bến Lức, … đặc biệt là đoàn sẽ đi ngang cầu Mỹ Thuận – cầu dây văng lớn nhất Việt Nam.</li><li>Quý khách dùng bữa sáng tại&nbsp;<strong>Trung Lương</strong>.</li><li>Đoàn tiếp tục hành trình khám phá và chiêm ngưỡng vẻ đẹp của&nbsp;<strong>Làng hoa Kiểng Tân Quy Đông - Làng hoa Sa Đéc.</strong>&nbsp;Một làng nghề truyền thống hơn trăm năm tuổi, nằm bên bờ sông Tiền được mệnh danh là xứ sở của các loài hoa kiểng với tổng diện tích trồng hoa hơn 510ha, với trên 2.300 hộ dân, 2.000 loài hoa kiểng khác nhau trở thành một trong những vựa hoa kiểng lớn nhất vùng đồng bằng sông Cửu Long và miền Nam.</li></ul><p class="ql-align-center"><br></p><p><br></p><p>Đoàn ghé thăm&nbsp;<strong>Vườn Hồng Tư Tôn,&nbsp;</strong>nơi được mệnh danh là&nbsp;<em>“Trái tim làng hoa”,&nbsp;</em>là nơi khai sinh hoa hồng Sa Đéc. Tới đây, Quý khách dễ dàng tạo ra những album hình sống động với những phân khu độc đáo:</p><ul><li>Tiểu Bali: Cổng trời Bali, xích đu Swing, …</li><li>Thung lũng hoa: với hơn 400 loài hoa đặc trưng Sadec</li><li>Khám phá cội nguồn làng hoa qua Giai Thoại Hoa Hồng Tư Tôn.</li></ul><p>Cùng những hoạt động bơi xuồng ngắm hoa, cho cá Koi bú bình sữa, …&nbsp;<em>(chi phí tự túc)</em></p><p><strong>Trưa</strong>&nbsp;&nbsp;Quý khách dùng cơm trưa với các món ăn đặc sản miền Tây.</p><ul><li class="ql-align-justify">Đoàn tiếp tục ghé thăm&nbsp;<strong>Vườn Quýt Hồng Lai Vung&nbsp;</strong>- được mệnh danh là&nbsp;<strong>“vương quốc quýt hồng”&nbsp;</strong>với diện tích trồng quýt hơn 2000ha, ở đây Quý khách có dịp thưởng thức quýt hồng, nước ép tươi từ quýt, trà đường thanh ngọt và tha hồ chụp hình với vườn quýt sai trĩu quả - có những trái sà xuống gần với cả mặt đất. Tại đây Quý khách tham quan, thỏa sức chụp hình, hoặc có thể mua về làm quà cho người thân và gia đình.</li><li class="ql-align-justify">Tham quan&nbsp;<strong>Chùa Phật Ngọc Xá Lợi,&nbsp;</strong>một trong những ngôi chùa thiêng liêng và lớn nhất tại&nbsp;<strong>Vĩnh Long.&nbsp;</strong>Chùa có kiến trúc nghệ thuật tinh xảo, vừa mang đậm tính tâm linh, lại mang nét kiến trúc truyền thống của Việt Nam.</li></ul><p class="ql-align-center"><br></p><ul><li class="ql-align-justify">Đoàn khởi hành về TP.HCM, trên đường Đoàn dừng chân ghé mua đặc sản địa phương</li></ul><p>Đoàn khởi hành về TP.HCM, trên đường Đoàn dừng chân ghé mua đặc sản địa phương. Đến TP.HCM. Kết thúc chuyến đi, chia tay đoàn và hẹn gặp lại Quý khách.&nbsp;&nbsp;.&nbsp;&nbsp;</p><h1><br></h1><p><br></p>',790000,0,10,'Đồng Tháp','Du lịch thông thường','Miền Nam','1 ngày'),
(19,'Du lịch  Cần Thơ','<p><strong>NGÀY 1 |&nbsp;TP. HCM – TIỀN GIANG – MỸ THO – CẦN THƠ (Ăn sáng, trưa, chiều)</strong></p><p class="ql-align-justify">Sáng Xe và Hướng Dẫn Viên Công Ty Du Lịch Việt đón Quý khách tại điểm hẹn.</p><p class="ql-align-justify">Khởi hành đi Mỹ Tho.&nbsp;</p><p class="ql-align-justify">Quý khách dùng bữa sáng tại Trung Lương. Tiếp tục lộ trình ghé tham quan chùa Vĩnh Tràng với lối kiến trúc kết hợp Châu Âu và Châu Á.</p><p class="ql-align-justify">Đoàn xuống bến đò 30/04, Quý khách xuống thuyền thưởng ngoạn cảnh sông nước miền Tây Nam Bộ với 4 cù lao: Long, Lân , Quy, Phụng, thưởng thức hương vị ngọt ngào của trái dừa xiêm.</p><p class="ql-align-justify">Thuyền đưa đoàn đi tham quan cù lao Thới Sơn, đi bộ trên đường làng, tham quan trại nuôi ong mật, uống trà mật ong, thưởng thức các loại mứt đặc sản. Tham quan vườn cây ăn trái, thưởng thức các loại trái cây tại vườn, nghe nhạc tài tử Nam Bộ. Đi xuồng chèo trong rạch dừa nước thiên nhiên.</p><p class="ql-align-justify">Thuyền lớn đưa khách sang Bến Tre tham quan lò kẹo dừa nổi tiếng Bến Tre.</p><p>Trưa Dùng cơm trưa.&nbsp;Thuyền đưa du khách trở lại Bến Tàu 30/4, khởi hành đi Cần Thơ.</p><p>Chiều Dùng cơm chiều. Nghỉ đêm khách sạn tại Cần Thơ.</p><p><strong>NGÀY 2 |&nbsp;CHỢ NỔI CÁI RĂNG – THIỀN VIỆN TRÚC LÂM – TP. HCM (Ăn sáng, trưa)</strong></p><p class="ql-align-justify">Sáng Du khách đến bến Ninh Kiều: Thuyền đưa du khách tham quan chợ nổi Cái Răng với cảnh nhộn nhịp buôn bán trên sông, một nét văn hóa rất đặc sắc ở vùng đồng bằng sông nước Cửu Long.</p><p class="ql-align-justify">Về lại khách sạn, dùng bữa sáng.&nbsp;</p><p class="ql-align-justify">Viếng thăm Thiền Viện Trúc Lâm Phương Nam được xem là một trong những công trình nghệ thuật độc đáo của đất Tây Đô - ngôi chùa rộng nhất khu vực Đồng bằng sông Cửu Long, được xây dựng trên diện tích gần 4 ha, với kết cấu lợp ngói, khung cột gỗ lim, chánh điện rộng cùng nhà tổ, nhiều tượng Phật được làm bằng gỗ Du Sam.&nbsp;</p><p class="ql-align-justify">Trưa Dùng cơm trưa.</p><p class="ql-align-justify">Khởi hành về TP.HCM trên dừng chân tham quan cầu treo Mỹ Thuận, tiếp tục lộ trình về đến TP.HCM</p><p class="ql-align-justify">Kết thúc hành trình tour. Chào tạm biệt và hẹn gặp lại Quý khách.</p><h1><br></h1><p><br></p>',1400000,0,10,'Cần Thơ','Du lịch thông thường','Miền Nam','2 ngày 1 đêm'),
(20,'Du lịch Quảng Ngãi','<p><strong>NGÀY 1 |&nbsp;TP.HCM – SÂN BAY CHU LAI - ĐẢO LÝ SƠN (Ăn trưa, chiều)</strong></p><p class="ql-align-justify"><strong>Sáng:</strong>&nbsp;Quý khách có mặt tại ga quốc nội, sân bay Tân Sơn Nhất trước giờ bay ít nhất ba tiếng.</p><ul><li class="ql-align-justify">Đại diện công ty Du Lịch Việt đón và hỗ trợ Quý Khách làm thủ tục đón chuyến bay đi Lý Sơn.&nbsp;&nbsp;&nbsp;Đến sân bay Chu Lai, Hướng dẫn viên đón đoàn khởi hành đến Cảng Sa Kỳ, hướng dẫn viên làm thủ tục cho đoàn lên tàu ra đảo Lý Sơn.&nbsp;</li></ul><p class="ql-align-justify"><strong>Trưa:</strong>&nbsp;Dùng bữa trưa. Đoàn về nhận phòng nghỉ ngơi .</p><ul><li class="ql-align-justify">Đoàn tiếp tục&nbsp;tham quan danh lam thắng cảnh đẹp bậc nhất tại đảo đó là: Chùa Hang hay còn gọi là “Thiên khổng thạch tự “ nôm na là chùa nằm trong hang ,nó được hình thành vô cùng độc đáo do quá trình phun trào núi lửa và quá trình xâm thực nước biển năm xưa tạo thành, trước chùa là 1 khung cảnh đầy thơ mộng với hàng cây Bàng Vuông cổ thụ hàng trăm năm, bãi biển là dãy cát san hô dài trắng xóa nước trong như thạch .</li><li class="ql-align-justify">Đoàn tham quan Cột Cờ Chủ Quyền nằm trên đỉnh núi Thới Lới, ở đây chúng ta có thể chiêm ngưỡng vẻ đẹp toàn cảnh của đảo Lý Sơn.&nbsp;&nbsp;&nbsp;&nbsp;</li><li class="ql-align-justify">Điểm tham quan tiếp theo trên đào là&nbsp;Hang Câu,&nbsp;là một hang động to nằm dưới vách núi của đỉnh Thới Lới, bên trên vách núi có đàn hải âu tung cách và&nbsp;khi chiều về quý khách sẽ được chiêm ngưỡng ánh hoàng hôn tuyệt đẹp ,và&nbsp;trước hang là bãi cát san hô dài với làn nước trong xanh, chúng kết hợp với nhau tạo nên một khung cảnh thơ mộng và tuyệt đẹp.&nbsp;&nbsp;</li></ul><p class="ql-align-justify"><strong>Chiều:</strong>&nbsp;Dùng bữa chiều. Nghỉ đêm tại Lý Sơn</p><ul><li class="ql-align-justify">Tự do khám phá đảo buổi tối.</li></ul><p><strong>NGÀY 2 |&nbsp;KHÁM PHÁ ĐẢO BÉ VÀ PHÍA TÂY ĐẢO LỚN (Ăn sáng, trưa, chiều)</strong></p><p class="ql-align-justify"><strong>Sáng:</strong>&nbsp;Dùng bữa sáng với món đặc sản trên đào&nbsp;</p><ul><li class="ql-align-justify">Hướng dẫn viên đưa quý khách đến với Đảo Bé, nơi đây được mệnh danh là Maldives của Việt Nam tại đây quý khách có thể thoải mái tắm biển dưới làn nước trong xanh và cùng nhau khám phá rạn san hô tuyệt đẹp của đảo, bãi cát trắng mịn kết hợp với hàng dừa cao ngút tạo nên một vẻ đẹp hoang sơ vô cùng đẹp.</li></ul><p class="ql-align-justify"><strong>Trưa:</strong>&nbsp;Dùng bữa trưa. Đoàn về nhận phòng nghỉ ngơi&nbsp;</p><ul><li class="ql-align-justify">Hướng dẫn viên tiếp tục đưa đoàn đi tham quan khám phá đảo Lý Sơn với các địa danh sau: bắt đầu tham quan điểm đầu tiên đó là : nhà trưng bày Hải Đội Hoàng Sa Bắc Hải, là nơi lưu giữ hơn hàng trăm hiện vật của những hùng binh năm xưa thể hiện chủ quyền biển đảo với hai quần đảo Trường Sa và Hoàng Sa.</li><li class="ql-align-justify">Đoàn tham quan ngôi chùa thứ hai đó là: Chùa Đục, là ngôi chùa vô cùng độc đáo của đảo&nbsp;nằm trên đỉnh núi Giáng Tiền. Thạch Cổng Tò Vò, là nơi có cấu tạo địa chất rất độc đáo, về chiều cảnh hoàng hôn buông xuống tạo nên một khung cảnh đẹp như tranh vẻ, sẽ làm cho du khách vô cùng thích thú.</li></ul><p class="ql-align-justify"><strong>Chiều:</strong>&nbsp;Dùng bữa chiều. Nghỉ đêm tại Lý Sơn</p><ul><li class="ql-align-justify">Tự do khám phá đảo buổi tối.</li></ul><p><strong>NGÀY 3 |&nbsp;LÝ SƠN – CẢNG SA KỲ - CHU LAI – HỒ CHÍ MINH (Ăn sáng, trưa)</strong></p><p class="ql-align-justify"><strong>Sáng:</strong>&nbsp;Dùng bữa sáng với món đặc sản trên đào</p><ul><li class="ql-align-justify">Quý khách tự do nghỉ ngơi tại khách sạn hoặc mua sắm đặc sản địa phương về làm quà cho gia đình, người thân.</li></ul><p class="ql-align-justify"><strong>Trưa:&nbsp;</strong>Dùng bữa trưa.</p><ul><li class="ql-align-justify">Đoàn trả phòng lên tàu về lại cảng Sa Kỳ&nbsp;</li><li class="ql-align-justify">Hướng dẫn viên tiễn đoàn ra sân bay Chu Lai&nbsp;đón chuyến bay về TP.HCM.</li><li class="ql-align-justify">Kết thúc chương trình tham quan, chia tay và hẹn gặp lại.</li></ul><p><br></p><h1><br></h1><p><br></p>',2399000,0,10,'Quảng Ngãi','Du lịch thông thường','Miền Trung','3 ngày 2 đêm');
