-- Tạo bảng FAQ
CREATE TABLE faq (
    faq_id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    faq_question VARCHAR(400) NOT NULL,
    faq_answer VARCHAR(900) NOT NULL
);


CREATE TABLE users (
    user_id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    user_firstname VARCHAR(100) NOT NULL,
    user_lastname VARCHAR(100) NOT NULL,
    user_phone VARCHAR(50) NOT NULL,
    user_username VARCHAR(100) NOT NULL,
    user_password VARCHAR(100) NOT NULL,
    user_role VARCHAR(50) NOT NULL
);



CREATE TABLE comments (
    comment_id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    tour_id INT NOT NULL,
    name_comment varchar(50),
    comment_content TEXT,
    comment_date DATE NOT NULL,
    FOREIGN KEY (tour_id) REFERENCES tours(tour_id)
);

-- insert dữ liệu vào bảng comments

CREATE TABLE email_sales (
    email_sale_id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    email_sale VARCHAR(100) NOT NULL
);
